import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { User } from '../components/user/user.model';

@Injectable({
  providedIn: 'root'
})
export class UserService {
  baseUrl = "http://localhost:5000/api/user"

  constructor(private http: HttpClient) { }

  searchBook(str: string): Observable<any> {
    let req = { "title": str }
    let url = `${this.baseUrl}/search`
    return this.http.post<User>(url, req).pipe(
      map(obj => obj),
      catchError(e => this.errorHandler(e))
    )
  }

  saveBook(book: User): Observable<User> {
    let req = { "book": book }
    return this.http.post<User>(this.baseUrl, req).pipe(
      map(obj => obj),
      catchError(e => this.errorHandler(e))
    )
  }

  getBooks(): Observable<User[]> {
    return this.http.get<User[]>(this.baseUrl).pipe(
      map(obj => obj),
      catchError(e => this.errorHandler(e))
    )
  }

  getBookById(id: string): Observable<User> {
    const url = `${this.baseUrl}/${id}`
    return this.http.get<User>(url).pipe(
      map(obj => obj),
      catchError(e => this.errorHandler(e))
    )
  }

  updateBook(book: User): Observable<User> {
    const url = `${this.baseUrl}`
    let req = { "book": book }
    return this.http.put<Book>(url, req).pipe(
      map(obj => obj),
      catchError(e => this.errorHandler(e))
    )
  }

  deleteBook(id: string): Observable<User> {
    let url = `${this.baseUrl}/${id}`
    return this.http.delete<User>(url).pipe(
      map(obj => obj),
      catchError(e => this.errorHandler(e))
    )
  }

  errorHandler(e: any): Observable<any> {
    alert(e.msg)
    return EMPTY
  }
}
