import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { Book } from 'src/app/models/book.model';
import { Borrow } from 'src/app/models/borrow.model';
import { User } from 'src/app/models/user.model';
import { BookService } from 'src/app/services/book.service';
import { BorrowService } from 'src/app/services/borrow.service';
import { UserService } from 'src/app/services/user.service';

@Component({
  selector: 'app-borrow-create',
  templateUrl: './borrow-create.component.html',
  styleUrls: ['./borrow-create.component.css']
})
export class BorrowCreateComponent implements OnInit {

  constructor(private router: Route, private userService: UserService, private bookService: BookService, private borrowService: BorrowService){ }

  users!: User[]
  books!: Book[]
  borrow!: Borrow

  ngOnInit(): void {
    this.userService.getUsers().subscribe(users => {
      this.users = users
    })
    this.bookService.getBooks().subscribe(books => {
      this.books = books
    })
  }

  newBorrow(): void {
    this.borrowService.newBorrow(this.borrow).subscribe(() => {
      alert('Book borrowed!')
      this.router.navigate(['/user'])
    })

  }

  cancel(): void {
    this.router.navigateByUrl('/user')

}
