import { Book } from './../components/book/book.model';
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { EMPTY, Observable } from 'rxjs';
import { catchError, map } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})

export class BookService {

  baseUrl = "http://localhost:5000/api/book"

  constructor(private http: HttpClient) { }

  searchBook(str: string): Observable<Book[]>{
    let req = {"title": str}
    return this.http.post<Book[]>
  }

  saveBook(book: Book): Observable<Book> {
    let req = { "book": book }
    return this.http.post<Book>(this.baseUrl, req).pipe(
      map(obj => obj),
      catchError(e => this.errorHandler(e))
    )
  }

  getBooks(): Observable<Book[]> {
    return this.http.get<Book[]>(this.baseUrl).pipe(
      map(obj => obj),
      catchError(e => this.errorHandler(e))
    )
  }

  getBookById(id: string): Observable<Book> {
    const url = `${this.baseUrl}/${id}`
    return this.http.get<Book>(url).pipe(
      map(obj => obj),
      catchError(e => this.errorHandler(e))
    )
  }

  updateBook(book: Book): Observable<Book> {
    const url = `${this.baseUrl}`
    let req = { "book": book }
    return this.http.put<Book>(url, req).pipe(
      map(obj => obj),
      catchError(e => this.errorHandler(e))
    )
  }

  deleteBook(id: string): Observable<Book> {
    let url = `${this.baseUrl}/${id}`
    return this.http.delete<Book>(url).pipe(
      map(obj => obj),
      catchError(e => this.errorHandler(e))
    )
  }

  errorHandler(e: any): Observable<any> {
    alert(e.msg)
    return EMPTY
  }
}
